package org.dobi.app.config; import org.dobi.kafka.consumer.KafkaConsumerService; import org.dobi.manager.MachineManagerService; import org.springframework.context.annotation.Bean; import org.springframework.context.annotation.Configuration; @Configuration @ComponentScan(basePackages = "org.dobi.manager") public class DobiServiceConfiguration { @Bean public MachineManagerService machineManagerService() { return new MachineManagerService(); } @Bean public KafkaConsumerService kafkaConsumerService(MachineManagerService m) { m.initializeKafka(); return new KafkaConsumerService(m.getAppProperty("kafka.bootstrap.servers"), "dobi-persistence-group", m.getAppProperty("kafka.topic.tags.data"), m.getEmf()); } }
