package org.dobi.app.service; import org.dobi.dto.MachineStatusDto; import org.dobi.manager.MachineManagerService; import org.springframework.stereotype.Service; import java.util.List; @Service public class SupervisionService { private final MachineManagerService mms; public SupervisionService(MachineManagerService mms) { this.mms = mms; } public List<MachineStatusDto> getAllMachineStatuses() { return mms.getActiveCollectorDetails(); } public void restartMachineCollector(Long id) { mms.restartCollector(id); } }
