package org.dobi.app.service; import org.dobi.app.dto.MachineStatusDto; import org.dobi.manager.MachineManagerService; import org.springframework.stereotype.Service; import java.util.List; @Service public class SupervisionService { private final MachineManagerService machineManagerService; public SupervisionService(MachineManagerService machineManagerService) { this.machineManagerService = machineManagerService; } public List<MachineStatusDto> getAllMachineStatuses() { return machineManagerService.getActiveCollectorDetails(); } public void restartMachineCollector(Long machineId) { machineManagerService.restartCollector(machineId); } }
